import React,{useState,useEffect} from "react";


const Weatherapp=()=>
{  const[city,setCity]=useState("Jabalpur");

    const[search,setSearch]=useState('Jabalpur');

    useEffect( ()=>
    {    const fetchApi=async ()=>{
        const url=`https://api.openweathermap.org/data/2.5/weather?q=${search}&units=metrics&appid=ce6a0e3aa65f58f2abc73e89a226a02a`
         const response = await fetch(url);
         const res= await response.json();
        console.log(res);
        setCity(res.main);
    }
        fetchApi();
    },[search])


    return(<>
   <div className='container' >
  <h1 align='center'>Weather Report</h1>
  <div className="inputData">
  <input type="search" className="search-bar" placeholder="Enter City name" onChange={(event)=>{setSearch(event.target.value)}} >
    </input>
    <Button variant="primary">Primary</Button>{' '}
</div>
{!city?
 (<h1>INVALID CITY NAME</h1>):
 <div> 
<div id="location" >{search},India</div>
<div id="time" >12:55</div>
<div id="day" >monday</div>
<div id="date" >30-12-2021</div>
<div id="temp" >{city.temp}</div>
</div>}
</div>
  
  </>
  )
}
export default Weatherapp;
