import React,{useState,useEffect} from "react";


const Weatherapp=()=>
{  const[city,setCity]=useState("null");

    const[search,setSearch]=useState('Jabalpur');

    useEffect( ()=>
    {    const fetchApi=async()=>{
        const url=`https://api.openweathermap.org/data/2.5/weather?q=${search}&appid=ce6a0e3aa65f58f2abc73e89a226a02a`
         const response =await fetch(url);
         const res= await response.JSON()
         console.log(res);
    }
        fetchApi();
    })


    return(<>
   <div className='container' >
  <h1 align='center'>Weather Report</h1>
  <div className="inputData">
  <input type="text" className="search-bar" placeholder="Enter City name" onChange={(event)=>{}} >
    </input>
  <button className="searchbutton" type="button" >search</button>
</div>
<div id="location" >{city},India</div>
<div id="time" >12:55</div>
<div id="day" >monday</div>
<div id="date" >30-12-2021</div>
<div id="temp" >20 degreeC</div>

</div>
  
  </>
  )
}
export default Weatherapp;
